using DtoLayer.Dtos.AppUserDtos;using FluentValidation;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;namespace BusinessLayer.ValidationRules{    public class AppUserRegisterDtoValidator : AbstractValidator<AppUserRegisterDto>    {        public AppUserRegisterDtoValidator()        {            RuleFor(a => a.Name)                .MaximumLength(30).WithMessage("En fazla 30 karakter girişi yapın")                .MinimumLength(2).WithMessage("Lütfen en az 2 karakter girişi yapın")                .NotEmpty().WithMessage("Ad alanı boş geçilemez");            RuleFor(a => a.Surname).NotEmpty().WithMessage("Soyad alanı boş geçilemez");            RuleFor(a => a.Email)                .EmailAddress().WithMessage("Lütfen geçerli bir mail adresi giriniz")                .NotEmpty().WithMessage("Email alanı boş geçilemez");            RuleFor(a => a.Password).NotEmpty().WithMessage("Şifre alanı boş geçilemez");            RuleFor(a => a.ConfirmPassword)                .Equal(y => y.Password).WithMessage("Parolalar eşleşmiyor")                .NotEmpty().WithMessage("Şifre tekrar alanı boş geçilemez");            RuleFor(a => a.UserName).NotEmpty().WithMessage("Soyad alanı boş geçilemez");        }    }}